    const addUserHandler = (userName, userAge) => {
        setUsersList((prevUsersList) => {});
    };

    return (
        <div>
            <AddUser onAddUser={addUserHandler} />
            <UsersList users={usersList} />
        </div>
    );
};

export default App;
